public class CreateProbePayload implements Runnable {



	@Override
	public void run() {
	}

	protected byte[] readContent(final ProcessSession session, final FlowFile flowFile) {
		final ByteArrayOutputStream baos = new ByteArrayOutputStream((int) flowFile.getSize() + 1);
		session.read(flowFile, new InputStreamCallback() {
			public void process(final InputStream in) throws IOException {
				StreamUtils.copy(in, baos);
			}
		});
		return baos.toByteArray();
	}



	public FlowFile runSnippet(FlowFile flowFile, ProcessSession session, ProcessContext context, Connection con,
			String cacheValue) {
		System.out.println("CUSTOM============****======== Inside runsnippet with flow file ========================"+flowFile);
		if(flowFile==null){
			return null;
		}
		byte[] bytes = readContent(session, flowFile);
		String json;
		try {

			String checkPerformance="Please check the network performance of the site mentioned below.";
			String currentThreshold="The current throughput recorded is ";
			StringBuilder sb=new StringBuilder();
