        String measurementUnit=null;
		measurementUnit=(String) map.getOrDefault("ulThresholdUnit",null);
		Double doubleValue=Double.valueOf(map.getOrDefault("value","0.0"));
		if(measurementUnit!=null&&measurementUnit.equalsIgnoreCase("kbps")){
		threshold=threshold*1024;
		doubleValue=doubleValue*1024;
		}
		formattedValue=roundDoubleValue(doubleValue);
			sb.append("<b> "+map.get("userName")+"</b>");
			sb.append(" has fallen below the configured threshold of ");
			sb.append("<b>"+threshold).append(" "+measurementUnit).append("</b> ").
			append("for Workorder ID <b> "+map.get("workorderid")+"</b>. ")
			.append(checkPerformance).append("The current upload throughput recorded is ")
			.append("<b>"+formattedValue).append(" "+measurementUnit+ "</b>.");
			map.put("error", lowUl+map.get("technology"));
			map.put("timeField", "Time");
			map.put("errorDescription", sb.toString());
			break;
		}
		case "Connectivity" :
		{
		if(map.get("status").equals("Network Switch")){
		map.put("error", "Connectivity Switch from 5G to LTE");
			map.put("errorDescription", String.format("The device <b> %s </b> is experiencing connectivity switch 5G to LTE network for Workorder ID <b> %s</b>. Please check 5G network availability on the site mentioned.",map.get("userName"),map.get("workorderid")));
			map.put("timeField", "Last Reported Time");
			map.put("lteDisplay","");
              map.put("display5g","");

		}
		else if(map.get("status").equals("Network Restore")){
		map.put("error", "Connectivity Switch from LTE to 5G");
			map.put("errorDescription", String.format("The device <b> %s </b> is experiencing connectivity Switch LTE to 5G network for Workorder ID <b> %s</b>. ",map.get("userName"),map.get("workorderid")));
			map.put("timeField", "Last Reported Time");
