			map.put("timeField", "Last Reported Time");
		}
		else{

		sb1.append("The device ");
		sb1.append("<b> "+map.get("userName")+"</b>");
	sb1.append(" is experiencing connectivity issues to the network");
	sb1.append(" for Workorder ID <b>"+map.get("workorderid")+"</b>.");
	sb1.append(" It has been ");
	sb1.append("<b> "+ map.get("lastReportedTime") +" minutes </b>");
	sb1.append(" since last reported connectivity. Please check network availability on the site mentioned.");
			map.put("error", "Connectivity Failure From "+map.get("technology"));
			map.put("errorDescription", sb1.toString());
			map.put("timeField", "Last Reported Time");
			}
			}
			break;
