		map.remove("executesql.query.duration");
		map.remove("filename");
		map.remove("RouteOnAttribute.Route");
		map.remove("AvailableCount");
		map.remove("executesql.row.count");
		map.remove("path");
		map.remove("ScheduledTime");
		map.remove("emailIds");
		map.remove("json");

		String technology=map.get("technology");

		if(technology!=null&&technology.equals("5G")){
		      map.putIfAbsent("lteDisplay","none");
              map.putIfAbsent("display5g","");

		}
		else {
		      map.putIfAbsent("lteDisplay","");
              map.putIfAbsent("display5g","none");	
		}
