	}
	public static String roundDoubleValue(Double value) {
	try {
		DecimalFormat df=new DecimalFormat("#.####");
		return value!=null?df.format(value):null;
		}
		catch(Exception e){
		e.printStackTrace();
		return null;		
	}
	}

}
